package com.cognizant.authapi.external.beans;

import com.fasterxml.jackson.annotation.JsonIgnore;
import lombok.AllArgsConstructor;
import lombok.Data;
import lombok.NoArgsConstructor;
import org.springframework.data.annotation.*;
import org.springframework.data.mongodb.core.mapping.Document;

import java.time.Instant;
import java.util.ArrayList;
import java.util.List;

/**
 * Created by 784420 on 6/2/2021 3:05 AM
 */
@Data
@Document(collection = "externalToolDetails")
@AllArgsConstructor
@NoArgsConstructor
public class ExternalToolDetails {
    @Id
    private String email;
    private List<ExternalToolConfig> configs = new ArrayList<>();

    public ExternalToolDetails(String email) {
        this.email = email;
    }

    @JsonIgnore
    @CreatedBy
    private String user;
    @JsonIgnore
    @CreatedDate
    private Instant createdDate;
    @JsonIgnore
    @LastModifiedBy
    private String lastModifiedUser;
    @JsonIgnore
    @LastModifiedDate
    private Instant lastModifiedDate;
}
